select u.login as user_login , u.name as user_name , u.company as user_company, u.location as user_location , p.url as repo_url , p.name as repo_name , pm.created_at as time_when_added , p.description as project_description , p.language as project_language, p.created_at as when_project_created , (select name from projects prj where prj.id=p.forked_from) as forked_from from project_members pm, projects p, users u where (pm.user_id = u.id) and (pm.repo_id = p.id) and (pm.created_at between '2012-01-01 00:00:01' and '2012-12-31 23:59:59') order by time_when_added asc